def void getData(String name1,String id1,String dept1,String age1)
{
def request1=testRunner.testCase.testSuite.testCases["GetEmpTestcase"].testSteps["getEmpTestStep"].getPropertyValue("Request")
def xmlAdd1=new XmlHolder(request1);
log.info"Call to getData of User--->$name1"
xmlAdd1.setNodeValue("//typ:getEmployeeDetails/typ:employeeName",name1)
def newxml1=xmlAdd1.getXml();
testRunner.testCase.testSuite.testCases["GetEmpTestcase"].testSteps["getEmpTestStep"].setPropertyValue("Request",newxml1)
def getdetails=testRunner.testCase.testSuite.testCases["GetEmpTestcase"].testSteps["getEmpTestStep"];
def context11=new WsdlTestRunContext(getdetails);
getdetails.run(testRunner,context11)
def response=testRunner.testCase.testSuite.testCases["GetEmpTestcase"].testSteps["getEmpTestStep"].getPropertyValue("Response")
def xmlresp=new XmlHolder(response);
def finalname=xmlresp.getNodeValue("//ns:return/ns:name")
def finalid=xmlresp.getNodeValue("//ns:return/ns:id")
def finaldept=xmlresp.getNodeValue("//ns:return/ns:department")
def finalage=xmlresp.getNodeValue("//ns:return/ns:age")
log.info "Details we received in getData for User $name1 are id=$finalid,name=$finalname,dept=$finaldept,age=$finalage"
assert name1==finalname
assert id1==finalid
assert dept1==finaldept
assert age1==finalage
}
