import jxl.*;
import com.eviware.soapui.support.XmlHolder;

File filepath=new File("D:\\SOAPProjects\\Test1.xls");
Workbook wb=Workbook.getWorkbook(filepath);
Sheet sheet=wb.getSheet(0);
int row=sheet.getRows();
log.info row

def name,id,dept,age;
for(int i=1;i<row;i++)
{
	name=sheet.getCell(0,i).getContents();
	id=sheet.getCell(1,i).getContents();
	dept=sheet.getCell(2,i).getContents();
	age=sheet.getCell(3,i).getContents();
	MyData(name,id,dept,age);

}

def void MyData(String name1,String id1,String dept1,String age1)
{
	def request=testRunner.testCase.testSuite.testCases['PractTest'].testSteps['PractAddEmp'].getPropertyValue("Request")
	log.info request
	def xmlAdd=new XmlHolder(request);
	xmlAdd.setNodeValue("//typ:addEmployee//typ:name",name1)
	xmlAdd.setNodeValue("//typ:addEmployee//typ:id",id1)
	xmlAdd.setNodeValue("//typ:addEmployee//typ:Department",dept1)
	xmlAdd.setNodeValue("//typ:addEmployee//typ:age",age1)
	def xmlset=xmlAdd.getXml();
	log.info xmlset
	testRunner.testCase.testSuite.testCases['PractTest'].testSteps['PractAddEmp'].setPropertyValue("Request",xmlset)
	def hit=testRunner.testCase.testSuite.testCases['PractTest'].testSteps['PractAddEmp'];
	hit.run(testRunner,context)

}

def void Getdata(String name1,String id1,String dept1,String age1)
{
	def request=testRunner.testCase.testSuite.testCases['PractTest'].testSteps['PractGetEmp'].getPropertyValue("Request")
	log.info request
	def xmlAdd=new XmlHolder(request);
	xmlAdd.setNodeValue("//typ:getEmployeeDetails//typ:employeeName",name1)
	def xmlset=xmlAdd.getXml();
	log.info xmlset
	testRunner.testCase.testSuite.testCases['PractTest'].testSteps['PractGetEmp'].setPropertyValue("Request",xmlset)
	def hit=testRunner.testCase.testSuite.testCases['PractTest'].testSteps['PractGetEmp'];
	hit.run(testRunner,context)

}
